import React, { useEffect, useState } from "react";
import { Col, Row } from "react-bootstrap";
import Sidebar from "../../Components/Sidebar";
import { DataGrid } from "@mui/x-data-grid";

import "./Complaints.css";
import axios from "axios";
import { Link } from "react-router-dom";

function Complaints() {
  
  const columns = [
    {
      field: "userName",
      headerName: "Customer name",
      width: 240,
    },
    {
      field: "email",
      headerName: "Email",
      width: 200,
      renderCell: (params) => {
        return <Link to={`/orders/${params.value}`}>{params.value}</Link>;
      },
    },
    {
      field: "id",
      headerName: "Order ID",
      width: 200,
      renderCell: (params) => {
        return <Link to={`/orders/${params.value}`}>{params.value}</Link>;
      },
    },
    {
      field: "status",
      headerName: "Order Status",
      width: 160,
    },
    { 
      field: "orderAmount",
      headerName: "Order Amount",
      width: 160,
    },
    {
      field: "orderedAt",
      headerName: "Order Date",
      width: 160,
    },{
      field:"complaint",
      headerName:"complaint",
      width:250,
    }
  ];
  const rows = [
    { id: 1, userId: 'Snow', userName: 'Jon', status: 35, orderAmount:200, orderAt:"10/11/2002" },
    { id: 2, userId: 'Lannister', userName: 'Cersei', status: 42, orderAmount:200, orderAt:"10/11/2002" },
    { id: 3, userId: 'Lannister', userName: 'Jaime', status: 45, orderAmount:200, orderAt:"10/11/2002" },
    { id: 4, userId: 'Stark', userName: 'Arya', status: 16, orderAmount:200, orderAt:"10/11/2002" },
    { id: 5, userId: 'Targaryen', userName: 'Daenerys', status: null, orderAmount:200, orderAt:"10/11/2002" },
    { id: 6, userId: 'Melisandre', userName: null, status: 150, orderAmount:200, orderAt:"10/11/2002" },
    { id: 7, userId: 'Clifford', userName: 'Ferrara', status: 44, orderAmount:200, orderAt:"10/11/2002" },
    { id: 8, userId: 'Frances', userName: 'Rossini', status: 36, orderAmount:200, orderAt:"10/11/2002" },
    { id: 9, userId: 'Roxie', userName: 'Harvey', status: 65, orderAmount:200, orderAt:"10/11/2002" },
  ];
  return (
    <div className="dashboard-parent-div">
      <Row>
        <Col lg={2}>
          <Sidebar />
        </Col>
        <Col className="complaints-content" lg={10}>
          <h4>Complaints & Feedbacks</h4>
          <p>
            Below are all the feedbacks and complaints provided by your
            customers.
          </p>
          <hr />
          <DataGrid rows={rows} columns={columns} />
        </Col>
      </Row>
    </div>
  );
}

export default Complaints;
