import React, { useEffect, useState } from "react";
import { Col, Row } from "react-bootstrap";
import Sidebar from "../../Components/Sidebar";
import { DataGrid } from "@mui/x-data-grid";

import "./Complaints.css";
import axios from "axios";
import { Link } from "react-router-dom";

function Complaints() {
  const columns = [
    {
      field: "userName",
      headerName: "Customer name",
      width: 240,
    },
    {
      field: "email",
      headerName: "Email",
      width: 200,
      renderCell: (params) => {
        return <Link to={`/orders/${params.value}`}>{params.value}</Link>;
      },
    },
    {
      field: "id",
      headerName: "Order ID",
      width: 200,
      renderCell: (params) => {
        return <Link to={`/orders/${params.value}`}>{params.value}</Link>;
      },
    },
    {
      field: "status",
      headerName: "Order Status",
      width: 160,
    },
    {
      field: "complaint",
      headerName: "Complaint",
      width: 400,
      renderCell: (params) => {
        return <div style={{ whiteSpace: "pre-wrap", wordWrap: "break-word" }}>{params.value}</div>;
      },
    },
  ];

  const rows = [
    {
      id: 1,
      userId: "Snow",
      userName: "Jon",
      status: 35,
      complaint:
        "adfjakfdsfjkafjdfjkabrakadabra adfjakfdsfjkafjdfjkabrakadabra adfjakfdsfjkafjdfjkabrakadabra adfjakfdsfjkafjdfjkabrakadabra",
    },
    // Other rows...
  ];

  return (
    <div className="dashboard-parent-div">
      <Row>
        <Col lg={2}>
          <Sidebar />
        </Col>
        <Col className="complaints-content" lg={10}>
          <h4>Complaints & Feedbacks</h4>
          <p>Below are all the feedbacks and complaints provided by your customers.</p>
          <hr />
          <div style={{ height: 500, width: "100%" }}>
            <DataGrid
              rows={rows}
              columns={columns}
              autoHeight
              checkboxSelection={false}
              disableSelectionOnClick
            />
          </div>
        </Col>
      </Row>
    </div>
  );
}

export default Complaints;
