import React, { useEffect, useRef, useState } from "react";
import { Card, Col, Form, Row } from "react-bootstrap";
import Sidebar from "../../Components/Sidebar";
import axios from "axios";

import "./AddProduct.css";

function AddProduct() {
  const [newProduct, setNewProduct] = useState({
    title: "",
    mrp: "",
    price: "",
    discount: "",
    S_stock: "",
    M_stock: "",
    L_stock: "",
    XL_stock: "",
    XXL_stock: "",
    category: "",
    description: "",
    images:[],
  });

  const [categories, setCategories] = useState([]);

  useEffect(() => {
    getCategories();
  }, []);

  const getCategories = () => {
    setCategories([]);
    axios({
      method: "get",
      url: "https://ecommerceappcj.herokuapp.com/api/categories/",
    }).then(function (response) {
      setCategories(response.data.categories);
    });
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setNewProduct((prev) => {
      return { ...prev, [name]: value };
    });
  };

  const [imagePreview, setImagePreview] = useState("");
  const [images, setImages] = useState([]);
  const imageButtonRef = useRef();
  const types = ["image/png", "image/jpeg", "image/jpg"];

  function handleImageChange(event) {
    let selectedFile = event.target.files[0];
    if (selectedFile && types.includes(selectedFile.type)) {
      setImages(selectedFile);
      setImagePreview(URL.createObjectURL(selectedFile));
    } else {
      setImages(null);
    }
  }

  const addProduct = async (event) => {
    event.preventDefault();
    try {
      const formData = new FormData();
      formData.append("title", newProduct.title);
      formData.append("mrp", newProduct.mrp);
      formData.append("price", newProduct.price);
      formData.append("discount", newProduct.discount);
      formData.append("S_stock", newProduct.S_stock);
      formData.append("M_stock", newProduct.M_stock);
      formData.append("L_stock", newProduct.L_stock);
      formData.append("XL_stock", newProduct.XL_stock);
      formData.append("XXL_stock", newProduct.XXL_stock);
      formData.append("category", newProduct.category);
      formData.append("description", newProduct.description);

      //to append image
      for (let i = 0; i < images.length; i++) {
        formData.append('images', images[i]);
      }
      // console.log(formData.get("title"));
      
      await axios({
        method: "post",
        url: "/products/add",
        data: formData,
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      }).then(async (response) => {
        if (response.status == 201) {
          setImagePreview();
          setNewProduct({
            title: "",
            mrp: "",
            price: "",
            discount: "",
            S_stock: "",
            M_stock: "",
            L_stock: "",
            XL_stock: "",
            XXL_stock: "",
            category: "",
            description: "",
          });
          alert("data added");
        }
        else {
          alert("data not added")
        }
      });
    } catch (err) {
      alert("Product not added to site. Please fill all the filds of form.")
      console.log("Error : " + err.message);
    }
  };

  //convert to base64
  function convertToBase64(e) {
    console.log(e);
    let reader = new FileReader();
    let selectedFiles = e.target.files;
    let base64Array = [];
  
    for(let i=0; i<selectedFiles.length ; i++)
    {
       reader.readAsDataURL(selectedFiles[i]);
  
       reader.onload = () =>{
        base64Array.push(reader.result);
      };
     
      if (base64Array.length === selectedFiles.length) {
        console.log(base64Array);
      }
  
    }
   
    
    reader.onerror = error =>{
      console.log("Error", error);
    };
  }


  return (
    <div className="dashboard-parent-div">
      <Row>
        <Col lg={2}>
          <Sidebar />
        </Col>
        <Col className="add-product-content" lg={10}>
          <h4>Add Product</h4>
          <p>
            Please fill the product details in the form below to add a new
            product.
          </p>
          <Card className="add-product-form-card">
            <Row>
              <Col>
                <div className="add-product-input-div">
                  <p>Product Name</p>
                  <input
                    type="text"
                    name="title"
                    value={newProduct.title}
                    onChange={handleChange}
                  ></input>
                </div>
              </Col>
              <Col>
                <div className="add-product-input-div">
                  <p>Product MRP</p>
                  <input
                    type="text"
                    name="mrp"
                    value={newProduct.mrp}
                    onChange={handleChange}
                  ></input>
                </div>
              </Col>
              <Col>
                <div className="add-product-input-div">
                  <p>Product Price</p>
                  <input
                    type="text"
                    name="price"
                    value={newProduct.price}
                    onChange={handleChange}
                  ></input>
                </div>
              </Col>
              <Col>
                <div className="add-product-input-div">
                  <p>Discount (in %)</p>
                  <input
                    type="text"
                    name="discount"
                    value={newProduct.discount}
                    onChange={handleChange}
                  ></input>
                </div>
              </Col>
            </Row>
            <Row>
              <Col>
                <div className="add-product-input-div">
                  <p>Product Category</p>
                  <select
                    className="add-product-dropdown"
                    name="category"
                    id="category"
                    value={newProduct.category}
                    onChange={handleChange}
                  >
                    <option className="add-product-dropdown-option">
                      option 1
                    </option>
                    <option className="add-product-dropdown-option">
                      option 2
                    </option>
                    <option className="add-product-dropdown-option">
                      option 3
                    </option>
                  </select>
                </div>
              </Col>
              <Col>
                <div className="add-product-input-div">
                  <p>Stock Quantity (S - Size)</p>
                  <input
                    type="number"
                    name="S_stock"
                    min={0}
                    value={newProduct.S_stock}
                    onChange={handleChange}
                  ></input>
                </div>
              </Col>
              <Col>
                <div className="add-product-input-div">
                  <p>Stock Quantity (M - Size)</p>
                  <input
                    type="number"
                    name="M_stock"
                    min={0}
                    value={newProduct.M_stock}
                    onChange={handleChange}
                  ></input>
                </div>
              </Col>
              <Col>
                <div className="add-product-input-div">
                  <p>Stock Quantity (L - Size)</p>
                  <input
                    type="number"
                    name="L_stock"
                    min={0}
                    value={newProduct.L_stock}
                    onChange={handleChange}
                  ></input>
                </div>
              </Col>
              <Col>
                <div className="add-product-input-div">
                  <p>Stock Quantity (XL - Size)</p>
                  <input
                    type="number"
                    name="XL_stock"
                    min={0}
                    value={newProduct.XL_stock}
                    onChange={handleChange}
                  ></input>
                </div>
              </Col>
              <Col>
                <div className="add-product-input-div">
                  <p>Stock Quantity (XXL - Size)</p>
                  <input
                    type="number"
                    name="XXL_stock"
                    min={0}
                    value={newProduct.XXL_stock}
                    onChange={handleChange}
                  ></input>
                </div>
              </Col>
            </Row>
            <Row>
              <Col>
                <div className="add-product-input-div">
                  <p>Product Description</p>
                  <textarea
                    rows={8}
                    name="description"
                    value={newProduct.description}
                    onChange={handleChange}
                  ></textarea>
                </div>
              </Col>
            </Row>
            <div className="add-product-input-div">
              <p>Select ALL Images of product</p>
              <input
              accept="image/*"
                type="file"
                multiple
                onChange={convertToBase64}
              />

            </div>
            <button onClick={addProduct} className="add-product-btn">
              Add Product
            </button>
          </Card>
        </Col>
      </Row>

    </div>
  );
}

export default AddProduct;
