import React, { useEffect, useState } from "react";
import { Col, Row } from "react-bootstrap";
import Sidebar from "../../Components/Sidebar";
import { DataGrid } from "@mui/x-data-grid";

import "./Complaints.css";
import axios from "axios";
// import { Link } from "react-router-dom";

const columns = [
  {
    field: "email",
    headerName: "Customer ID",
    width: 250,
    autoHeight: true,
    // renderCell: (params) => {
    //   return <Link to={`/users/${params.value}`}>{params.value}</Link>;
    // },
  },
  {
    field: "name",
    headerName: "Customer name",
    width: 240,
    autoHeight: true,
  },
  {
    field: "number",
    headerName: "Customer Phone",
    width: 180,
    autoHeight: true,
  },
  {
    field: "orderId",
    headerName: "Order ID",
    width: 100,
    autoHeight: true,
    // renderCell: (params) => {
    //   return <Link to={`/orders/${params.value}`}>{params.value}</Link>;
    // },
  },
  {
    field: "complaint",
    headerName: "Feedback & Complaints",
    width: 250,
    autoHeight: true,
  },
];


function Complaints() {
  const [complaints, setComplaints] = useState([]);
  //set loading
  const [loading, setLoading] = useState(true);

  let rows = [{
    email:"ad",
    name:"da",
    number:"dfa",
    orderId:"adfa",
    complaint:"dafdkfajlf"
  }];

  async function setRaws(item, index) {
    rows = [...rows, item];
  }
  const getComplaints = async () => {
    try {
      
      const response = await axios.get("/admin/complaints");

      setComplaints((prevComplaints) => [
        ...prevComplaints,
        response.data,
      ]);
      setLoading(false);
      console.log(complaints);
    } catch (err) {
      console.log(err);
    }
  };


  useEffect(() => {
    setComplaints([]);
    getComplaints();
  }, []);



  return (
    <div className="dashboard-parent-div">
      <Row>
        <Col lg={2}>
          <Sidebar />
        </Col>
        <Col className="complaints-content" lg={10}>
          <h4>Complaints & Feedbacks</h4>
          <p>
            Below are all the feedbacks and complaints provided by your
            customers.
          </p>
          <hr />
          {
            loading ?
              <h1>Loading</h1> :
              <DataGrid rows={rows}
                columns={columns}
                getRowHeight={() => 'auto'}
              />
          }

        </Col>
      </Row>
    </div>
  );
}

export default Complaints;
