import React, { useEffect, useState } from "react";
import { Col, Row } from "react-bootstrap";
import Sidebar from "../../Components/Sidebar";
import { DataGrid } from "@mui/x-data-grid";

import "./Complaints.css";
import axios from "axios";
import { Link } from "react-router-dom";

function Complaints() {
  
  const columns = [
    {
      field: "userName",
      headerName: "Customer name",
      width: 240,
    },
    {
      field: "email",
      headerName: "Email",
      width: 200,
      renderCell: (params) => {
        return <Link to={`/orders/${params.value}`}>{params.value}</Link>;
      },
    },
    {
      field: "id",
      headerName: "Order ID",
      width: 200,
      renderCell: (params) => {
        return <Link to={`/orders/${params.value}`}>{params.value}</Link>;
      },
    },
    {
      field: "status",
      headerName: "Order Status",
      width: 160,
    },
    {
      field:"complaint",
      headerName:"complaint",
      width:250,
      renderCell: (params) => {
        return (
          <div className="complaint-cell">
            <span>{params.value}</span>
          </div>
        );
      },
    }
  ];
  const rows = [
    { id: 1, userId: 'Snow', userName: 'Jon', status: 35,complaint:"adfjakfdsfjkafjdfjkabradddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddkadabra" },
    { id: 2, userId: 'Lannister', userName: 'Cersei', status: 42 },
    { id: 3, userId: 'Lannister', userName: 'Jaime', status: 45 },
    { id: 4, userId: 'Stark', userName: 'Arya', status: 16 },
    { id: 5, userId: 'Targaryen', userName: 'Daenerys', status: null },
    { id: 6, userId: 'Melisandre', userName: null, status: 150 },
    { id: 7, userId: 'Clifford', userName: 'Ferrara', status: 44 },
    { id: 8, userId: 'Frances', userName: 'Rossini', status: 36 },
    { id: 9, userId: 'Roxie', userName: 'Harvey', status: 65 },
  ];
  return (
    <div className="dashboard-parent-div">
      <Row>
        <Col lg={2}>
          <Sidebar />
        </Col>
        <Col className="complaints-content" lg={10}>
          <h4>Complaints & Feedbacks</h4>
          <p>
            Below are all the feedbacks and complaints provided by your
            customers.
          </p>
          <hr />
          <DataGrid autoHeight
              checkboxSelection={false}
              disableSelectionOnClick 
              rows={rows} columns={columns} />
        </Col>
      </Row>
    </div>
  );
}

export default Complaints;
