import React, { useEffect, useState } from "react";
import { Col, Row } from "react-bootstrap";
import Sidebar from "../../Components/Sidebar";
import { DataGrid } from "@mui/x-data-grid";

import "./Complaints.css";
import axios from "axios";
// import { Link } from "react-router-dom";

function Complaints() {
  const [complaints, setComplaints]=useState([]);
  let raws = [];
  useEffect(() => {
    getComplaints();
  }, []);

  async function setRaws(){
   
  }
  const getComplaints = async () => {
    setComplaints([]);
    await axios({
      method: "get",
      url: "/admin/complaints",
    }).then((response) => {
       setComplaints(response.data);
       complaints.forEach(setRaws);
    });
  };



  const columns = [
    {
      field: "userId",
      headerName: "Customer ID",
      width: 250,
      autoHeight: true,
      // renderCell: (params) => {
      //   return <Link to={`/users/${params.value}`}>{params.value}</Link>;
      // },
    },
    {
      field: "userName",
      headerName: "Customer name",
      width: 240,
      autoHeight: true,
    },
    {
      field: "userPhone",
      headerName: "Customer Phone",
      width: 180,
      autoHeight: true,
    },
    {
      field: "id",
      headerName: "Order ID",
      width: 100,
      autoHeight: true,
      // renderCell: (params) => {
      //   return <Link to={`/orders/${params.value}`}>{params.value}</Link>;
      // },
    },
    {
      field: "complaint",
      headerName: "Feedback & Complaints",
      width: 250,
      autoHeight: true,
    },
  ];
  
  return (
    <div className="dashboard-parent-div">
      <Row>
        <Col lg={2}>
          <Sidebar />
        </Col>
        <Col className="complaints-content" lg={10}>
          <h4>Complaints & Feedbacks</h4>
          <p>
            Below are all the feedbacks and complaints provided by your
            customers.
          </p>
          <hr />
         
          <DataGrid rows={rows}
           columns={columns}
           getRowHeight={() => 'auto'}
            />
        </Col>
      </Row>
    </div>
  );
}

export default Complaints;
