import React, { useEffect, useRef, useState } from "react";
import { Card, Col, Form, Row } from "react-bootstrap";
import Sidebar from "../../Components/Sidebar";
import axios from "axios";
import { useParams } from "react-router-dom";
import "./EditProduct.css";
// import { useNavigate } from "react-router-dom";

function EditProduct(props) {
  const [productData, setProductData] = useState();
  const [categories, setCategories] = useState([]);

  const [imagePreview, setImagePreview] = useState("");
  const [image, setImage] = useState(null);
  const imageButtonRef = useRef();
  const types = ["image/png", "image/jpeg", "image/jpg"];
  const { productId } = useParams("");
  // const navigate = useNavigate();

  useEffect(() => {
    // getCategories();
    getProduct();
  }, []);

  const getProduct = () => {
    setProductData();
    axios({
      method: "get",
      url: `/products/edit/${productId}`,
    })
      .then((response) => {
        // alert("hi");
        // console.log(response.data);
        setProductData(response.data);
        console.log(productData);
      })
      .catch((err) => {
        console.log("Error : " + err.message);
      });
  };

  // const getCategories = () => {
  //   setCategories([]);
  //   axios({
  //     method: "get",
  //     url: "https://ecommerceappcj.herokuapp.com/api/categories/",
  //   }).then(function (response) {
  //     setCategories(response.data.categories);
  //   });
  // };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setProductData((prev) => {
      return { ...prev, [name]: value };
    });
  };

  function handleImageChange(event) {
    let selectedFile = event.target.files[0];
    if (selectedFile && types.includes(selectedFile.type)) {
      setImage(selectedFile);
      setImagePreview(URL.createObjectURL(selectedFile));
    } else {
      setImage(null);
    }
  }

  const editProduct = async (event) => {
    event.preventDefault();
    try {
      axios({
        method: "patch",
        url: `products/update/${productId}`,
        data: {
          name: productData.name,
          price: productData.price,
          stockQuantity: productData.stockQuantity,
          categoryName: productData.category,
          description: productData.description,
        },
      }).then((response) => {
        if (image) {
          const formData = new FormData();
          formData.append("image", image);
          axios({
            method: "patch",
            url: `https://ecommerceappcj.herokuapp.com/api/products/update/image/${productId}`,
            data: formData,
          }).then((res) => {
            setImagePreview();
            setProductData({
              name: "",
              price: "",
              stock: "",
              category: "",
              desc: "",
            });
            // navigate("/products");
          });
        } else {
          setImagePreview();
          setProductData({
            name: "",
            price: "",
            stock: "",
            category: "",
            desc: "",
          });
          // navigate("/products");
        }
      });
    } catch (err) {
      console.log("Error : " + err.message);
    }
  };

  return (
    <div className="dashboard-parent-div">
      <Row>
        <Col lg={2}>
          <Sidebar />
        </Col>
        <Col className="add-product-content" lg={10}>
          <h4>Update Product</h4>
          {productData && (
            <p>
              Please fill the product details in the form below to update{" "}
              <strong>{productData.title}</strong>.
            </p>
          )}
          <Card className="add-product-form-card">
            {productData && (
              <div>
                <Row>
                  <Col>
                    <div className="add-product-input-div">
                      <p>Product Name</p>
                      <input
                        type="text"
                        name="name"
                        value={productData.title}
                        onChange={handleChange}
                      ></input>
                    </div>
                  </Col>
                  <Col>
                    <div className="add-product-input-div">
                      <p>Product MRP</p>
                      <input
                        type="number"
                        name="mrp"
                        value={productData.mrp}
                        onChange={handleChange}
                      ></input>
                    </div>
                  </Col>
                  <Col>
                    <div className="add-product-input-div">
                      <p>Product Price</p>
                      <input
                        type="text"
                        name="price"
                        value={productData.price}
                        onChange={handleChange}
                      ></input>
                    </div>
                  </Col>
                  <Col>
                    <div className="add-product-input-div">
                      <p>Discount (in %)</p>
                      <input
                        type="number"
                        name="discount"
                        value={productData.discount}
                        onChange={handleChange}
                      ></input>
                    </div>
                  </Col>
                </Row>
                <Row>
                  <Col>
                    <div className="add-product-input-div">
                      <p>Product Category</p>
                      <select
                        className="add-product-dropdown"
                        name="category"
                        id="category"
                        value={productData.category}
                        onChange={handleChange}
                      >
                        <option className="add-product-dropdown-option">
                          select option
                        </option>
                        <option className="add-product-dropdown-option">
                          option 1
                        </option>
                        <option className="add-product-dropdown-option">
                          option 2
                        </option>
                        <option className="add-product-dropdown-option">
                          option 3
                        </option>
                      </select>
                    </div>
                  </Col>
                  <Col>
                    <div className="add-product-input-div">
                      <p>Stock Quantity (S - Size)</p>
                      <input
                        type="number"
                        name="S_stock"
                        min={0}
                        value={productData.S_stock}
                        onChange={handleChange}
                      ></input>
                    </div>
                  </Col>
                  <Col>
                    <div className="add-product-input-div">
                      <p>Stock Quantity (M - Size)</p>
                      <input
                        type="number"
                        name="M_stock"
                        min={0}
                        value={productData.M_stock}
                        onChange={handleChange}
                      ></input>
                    </div>
                  </Col>
                  <Col>
                    <div className="add-product-input-div">
                      <p>Stock Quantity (L - Size)</p>
                      <input
                        type="number"
                        name="L_stock"
                        min={0}
                        value={productData.L_stock}
                        onChange={handleChange}
                      ></input>
                    </div>
                  </Col>
                  <Col>
                    <div className="add-product-input-div">
                      <p>Stock Quantity (XL - Size)</p>
                      <input
                        type="number"
                        name="XL_stock"
                        min={0}
                        value={productData.XL_stock}
                        onChange={handleChange}
                      ></input>
                    </div>
                  </Col>
                  <Col>
                    <div className="add-product-input-div">
                      <p>Stock Quantity (XXL - Size)</p>
                      <input
                        type="number"
                        name="XXL_stock"
                        min={0}
                        value={productData.XXL_stock}
                        onChange={handleChange}
                      ></input>
                    </div>
                  </Col>
                </Row>
                <Row>
                  <Col>
                    <div className="add-product-input-div">
                      <p>Product Description</p>
                      <textarea
                        rows={8}
                        name="description"
                        value={productData.description}
                        onChange={handleChange}
                      ></textarea>
                    </div>
                  </Col>
                </Row>
                <Row>
                  <p>
                    Paste Urls of Image. To get url please &nbsp;
                    <a href="https://imgur.com/upload" target="__blank"> click here</a>
                    .
                  </p>
                  <div className="add-product-input-div">
                    <input type="text" name="url1" value={productData.images[1]} />
                  </div>
                  {/* <div className="add-product-input-div">
                    <input type="text" name="url2" placeholder="Image url" onChange={imagesHandle} />
                  </div>
                  <div className="add-product-input-div">
                    <input type="text" name="url3" placeholder="Image url" onChange={imagesHandle} />
                  </div>
                  <div className="add-product-input-div">
                    <input type="text" name="url4" placeholder="Image url" onChange={imagesHandle} />
                  </div>
                  <div className="add-product-input-div">
                    <input type="text" name="url5" placeholder="Image url" onChange={imagesHandle} />
                  </div>
                  <div className="add-product-input-div">
                    <input type="text" name="url6" placeholder="Image url" onChange={imagesHandle} />
                  </div> */}
                </Row>
                <button onClick={editProduct} className="add-product-btn">
                  Update Product
                </button>
              </div>
            )}
          </Card>
        </Col>
      </Row>
    </div>
  );
}

export default EditProduct;
