import React, { useEffect, useRef, useState } from "react";
import { Card, Col, Form, Row } from "react-bootstrap";
import Sidebar from "../../Components/Sidebar";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from "axios";

import "./AddProduct.css";

function AddProduct() {
  const [newProduct, setNewProduct] = useState({
    title: "",
    mrp:"",
    price: "",
    discount:"",
    S_stock:"",
    M_stock:"",
    L_stock:"",
    XL_stock:"",
    XXL_stock:"",
    category: "",
    description: "",
  });

  const [categories, setCategories] = useState([]);

  useEffect(() => {
    getCategories();
  }, []);

  const getCategories = () => {
    setCategories([]);
    axios({
      method: "get",
      url: "https://ecommerceappcj.herokuapp.com/api/categories/",
    }).then(function (response) {
      setCategories(response.data.categories);
    });
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setNewProduct((prev) => {
      return { ...prev, [name]: value };
    });
  };

  const [imagePreview, setImagePreview] = useState("");
  const [image, setImage] = useState(null);
  const imageButtonRef = useRef();
  const types = ["image/png", "image/jpeg", "image/jpg"];

  function handleImageChange(event) {
    let selectedFile = event.target.files[0];
    if (selectedFile && types.includes(selectedFile.type)) {
      setImage(selectedFile);
      setImagePreview(URL.createObjectURL(selectedFile));
    } else {
      setImage(null);
    }
  }

  const addProduct = async (event) => {
    event.preventDefault();
    try {
      const formData = new FormData();
      formData.append("title", newProduct.title);
      formData.append("mrp", newProduct.mrp);
      formData.append("price", newProduct.price);
      formData.append("discount", newProduct.discount);
      formData.append("S_stock", newProduct.S_stock);
      formData.append("M_stock", newProduct.M_stock);
      formData.append("L_stock", newProduct.L_stock);
      formData.append("XL_stock", newProduct.XL_stock);
      formData.append("XXL_stock", newProduct.XXL_stock);
      formData.append("category", newProduct.category);
      formData.append("description", newProduct.description);
      // console.log(formData.get("title"));
      const res = await axios({
        method: "post",
        url: "/products/add",
        data: formData,
      }).then(async (response) => {
        const data = await res.json();
        if(!data || res.status == 422){
          toast.warning("Data not added", {
            position: "top-center",
        });
        }else{
          toast.success("data succefully added", {
            position: "top-center",
        });
        }
        setImagePreview();
        setNewProduct({
          title: "",
          mrp:"",
          price: "",
          discount:"",
          S_stock:"",
          M_stock:"",
          L_stock:"",
          XL_stock:"",
          XXL_stock:"",
          category: "",
          description: "",
        });
      
      });

      
    } catch (err) {
      console.log("Error : " + err.message);
    }
  };

  return (
    <div className="dashboard-parent-div">
      <Row>
        <Col lg={2}>
          <Sidebar />
        </Col>
        <Col className="add-product-content" lg={10}>
          <h4>Add Product</h4>
          <p>
            Please fill the product details in the form below to add a new
            product.
          </p>
          <Card className="add-product-form-card">
            <Row>
              <Col>
                <div className="add-product-input-div">
                  <p>Product Name</p>
                  <input
                    type="text"
                    name="title"
                    value={newProduct.title}
                    onChange={handleChange}
                  ></input>
                </div>
              </Col>
              <Col>
                <div className="add-product-input-div">
                  <p>Product MRP</p>
                  <input
                    type="text"
                    name="mrp"
                    value={newProduct.mrp}
                    onChange={handleChange}
                  ></input>
                </div>
              </Col>
              <Col>
                <div className="add-product-input-div">
                  <p>Product Price</p>
                  <input
                    type="text"
                    name="price"
                    value={newProduct.price}
                    onChange={handleChange}
                  ></input>
                </div>
              </Col>
              <Col>
                <div className="add-product-input-div">
                  <p>Discount (in %)</p>
                  <input
                    type="text"
                    name="discount"
                    value={newProduct.discount}
                    onChange={handleChange}
                  ></input>
                </div>
              </Col>
            </Row>
            <Row>
              <Col>
                <div className="add-product-input-div">
                  <p>Product Category</p>
                  <select
                    className="add-product-dropdown"
                    name="category"
                    id="category"
                    value={newProduct.category}
                    onChange={handleChange}
                  >
                    <option className="add-product-dropdown-option">
                      option 1
                    </option>
                    <option className="add-product-dropdown-option">
                      option 2
                    </option>
                    <option className="add-product-dropdown-option">
                      option 3
                    </option>
                  </select>
                </div>
              </Col>
              <Col>
                <div className="add-product-input-div">
                  <p>Stock Quantity (S - Size)</p>
                  <input
                    type="number"
                    name="S_stock"
                    min={0}
                    value={newProduct.S_stock}
                    onChange={handleChange}
                  ></input>
                </div>
              </Col>
              <Col>
                <div className="add-product-input-div">
                  <p>Stock Quantity (M - Size)</p>
                  <input
                    type="number"
                    name="M_stock"
                    min={0}
                    value={newProduct.M_stock}
                    onChange={handleChange}
                  ></input>
                </div>
              </Col>
              <Col>
                <div className="add-product-input-div">
                  <p>Stock Quantity (L - Size)</p>
                  <input
                    type="number"
                    name="L_stock"
                    min={0}
                    value={newProduct.L_stock}
                    onChange={handleChange}
                  ></input>
                </div>
              </Col>
              <Col>
                <div className="add-product-input-div">
                  <p>Stock Quantity (XL - Size)</p>
                  <input
                    type="number"
                    name="XL_stock"
                    min={0}
                    value={newProduct.XL_stock}
                    onChange={handleChange}
                  ></input>
                </div>
              </Col>
              <Col>
                <div className="add-product-input-div">
                  <p>Stock Quantity (XXL - Size)</p>
                  <input
                    type="number"
                    name="XXL-_stock"
                    min={0}
                    value={newProduct.stock}
                    onChange={handleChange}
                  ></input>
                </div>
              </Col>
            </Row>
            <Row>
              <Col>
                <div className="add-product-input-div">
                  <p>Product Description</p>
                  <textarea
                    rows={8}
                    name="description"
                    value={newProduct.description}
                    onChange={handleChange}
                  ></textarea>
                </div>
              </Col>
            </Row>
            <div className="add-product-input-div">
              <p>Paste Urls of Image</p>

            </div>
            <button onClick={addProduct} className="add-product-btn">
              Add Product
            </button>
          </Card>
        </Col>
      </Row>
      <ToastContainer />
    </div>
  );
}

export default AddProduct;
